using FluentAssertions;using ProjectService.Controllers;using ProjectService.Domain.Enums;using Xunit;namespace ProjectService.Tests;public class AdminAppointmentsControllerMapStatusTests{    [Theory]    [InlineData("CONFIRMED", TaskStatus.Accepted)]    [InlineData("IN_PROGRESS", TaskStatus.InProgress)]    [InlineData("COMPLETED", TaskStatus.Completed)]    [InlineData("CANCELLED", TaskStatus.Cancelled)]    [InlineData(null, TaskStatus.Requested)]    [InlineData("", TaskStatus.Requested)]    [InlineData("other", TaskStatus.Requested)]    public void MapAppointmentStatus_MapsStrings(string? input, TaskStatus expected){        var method = typeof(AdminAppointmentsController).GetMethod("MapAppointmentStatus", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Static)!;        var result = method.Invoke(null, new object?[]{ input });        result.Should().Be(expected);    }}