using FluentAssertions;using ProjectService.Dtos;using ProjectService.Validators;using Xunit;namespace ProjectService.Tests;public class AdminProjectsControllerApproveValidationTests{    [Fact]    public void ApproveProjectRequestValidator_RequiresTaskId(){        var validator = new ApproveProjectRequestValidator();        var req = new ApproveProjectRequest{ Tasks={ new ApproveProjectTaskUpdate{ TaskId=System.Guid.Empty } } };        var result = validator.Validate(req);        result.IsValid.Should().BeFalse();    }    [Fact]    public void ApproveProjectRequestValidator_ValidRequest(){        var validator = new ApproveProjectRequestValidator();        var req = new ApproveProjectRequest{ Tasks={ new ApproveProjectTaskUpdate{ TaskId=System.Guid.NewGuid() } } };        var result = validator.Validate(req);        result.IsValid.Should().BeTrue();    }}