using System;using System.Linq;using System.Threading;using System.Threading.Tasks;using FluentAssertions;using Microsoft.EntityFrameworkCore;using Microsoft.Extensions.Logging;using Moq;using ProjectService.Data;using ProjectService.Seeding;using Xunit;namespace ProjectService.Tests;public class DemoDataSeederTests{    [Fact]    public async Task SeedAsync_PopulatesData_WhenEmpty(){        var options = new DbContextOptionsBuilder<AppDb>().UseInMemoryDatabase("seed-db").Options;        var db = new AppDb(options);        var logger = new Mock<ILogger<DemoDataSeeder>>();        var seeder = new DemoDataSeeder(db, logger.Object);        await seeder.SeedAsync(CancellationToken.None);        db.Projects.Count().Should().Be(1);        db.Tasks.Count().Should().BeGreaterThan(0);        db.Activity.Count().Should().BeGreaterThan(0);    }    [Fact]    public async Task SeedAsync_Skips_WhenDataExists(){        var options = new DbContextOptionsBuilder<AppDb>().UseInMemoryDatabase("seed-db2").Options;        var db = new AppDb(options);        db.Projects.Add(new ProjectService.Domain.Entities.Project{ ProjectId=Guid.NewGuid(), Title="Existing" });        await db.SaveChangesAsync();        var logger = new Mock<ILogger<DemoDataSeeder>>();        var seeder = new DemoDataSeeder(db, logger.Object);        await seeder.SeedAsync(CancellationToken.None);        db.Projects.Count().Should().Be(1);    }}