using FluentAssertions;using ProjectService.Domain.Enums;using ProjectService.Dtos;using ProjectService.Validators;using Xunit;namespace ProjectService.Tests;public class TasksControllerMapStatusTests{    [Fact]    public void UpdateTaskStatusRequestValidator_AllowsLongNoteWithinLimit(){        var validator = new UpdateTaskStatusRequestValidator();        var req = new UpdateTaskStatusRequest{ Status=TaskStatus.InProgress, Note=new string('a', 1000)};        var result = validator.Validate(req);        result.IsValid.Should().BeTrue();    }    [Fact]    public void UpdateTaskStatusRequestValidator_FailsTooLongNote(){        var validator = new UpdateTaskStatusRequestValidator();        var req = new UpdateTaskStatusRequest{ Status=TaskStatus.InProgress, Note=new string('a', 1001)};        var result = validator.Validate(req);        result.IsValid.Should().BeFalse();    }}